package com.sing.ren.dao.table;import java.util.ArrayList;import java.util.List;import java.util.Map;import org.apache.commons.collections.MapUtils;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.Account;@Componentpublic class AccountDAO extends BaseDao {	@SuppressWarnings("deprecation")	public void insertDB(Account dto) throws Exception {		logger.debug("AccountDAO insertAccount");		getSqlMapClientTemplate().insert( nsForTableStatements("account_insert"), dto);	}		public List<Account> getAll(Map<String, Object> map) throws Exception {		logger.debug("AccountDAO getAll");		List<Map<String, Object>> resultMapList = query(map, nsForTableStatements("account_query"));		List<Account> result = new ArrayList<Account>();		for (Map<String, Object> resultMap : resultMapList){			result.add( mapToObject(resultMap) );		}		return result;	}	public void deleteDB(Map<String, Object> map) throws Exception {		logger.debug("AccountDAO deleteAccount");		delete(map, nsForTableStatements("account_delete"));	}	public Account mapToObject(Map<String, Object> map) throws Exception {		Account obj = new Account();		obj.setAccount( MapUtils.getString(map, "account", "") );		obj.setPassword( MapUtils.getString(map, "password", "") );		obj.setRole( MapUtils.getString(map, "role", "") ); 	return obj;	}}