package com.sing.ren.dao.table;import java.util.List;import java.util.Map;import org.apache.commons.collections.MapUtils;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.Account;@Componentpublic class AccountDAO extends BaseDao {	@SuppressWarnings("deprecation")	public List<Account> query(Account dto) throws Exception {		logger.debug("AccountDAO queryAccount");		return getSqlMapClientTemplate().queryForList(nsForTableStatements("account_query"), dto);	}	@SuppressWarnings("deprecation")	public void insert(Account dto) throws Exception {		logger.debug("AccountDAO insertAccount");		getSqlMapClientTemplate().insert( nsForTableStatements("account_insert"), dto);	}	@SuppressWarnings("deprecation")	public void update(Account dto) throws Exception {		logger.debug("AccountDAO updateAccount");		getSqlMapClientTemplate().queryForList(nsForTableStatements("account_update"), dto);	}	@SuppressWarnings("deprecation")	public void delete(Account dto) throws Exception {		logger.debug("AccountDAO AccountDetail");		getSqlMapClientTemplate().queryForList(nsForTableStatements("account_delete"), dto);	}	public Account mapToObject(Map<String, Object> map) throws Exception {		Account obj = new Account();		obj.setAccount( MapUtils.getString(map, "account", "") );		obj.setPassword( MapUtils.getString(map, "password", "") );		obj.setRole( MapUtils.getString(map, "role", "") );	return obj;	}}