package com.sing.ren.dao.table;import java.util.List;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.Account;@Componentpublic class AccountDAO extends BaseDao {	@Override	public String statementIdForInsert() throws Exception {		return nsForTableStatements("account_insert");	}	@Override	public String statementIdForQuery() throws Exception {		return nsForTableStatements("account_query");	}	@Override	public String statementIdForUpdate() throws Exception {		return nsForTableStatements("account_update");	}	@Override	public String statementIdForUpsert() throws Exception {		return nsForTableStatements("account_upsert");	}	@Override	public String statementIdForDelete() throws Exception {		return nsForTableStatements("account_delete");	}	public void insertDB(Account pojo) throws Exception {		logger.debug("AccountDAO insertAccount");		insert(pojo);	}	@SuppressWarnings("unchecked")	public List<Account> queryDB(Account pojo) throws Exception {		logger.debug("AccountDAO queryAccount");		return (List<Account>) query(pojo);	}	public void updateDB(Account pojo) throws Exception{		logger.debug("AccountDAO updateAccount");		upsert(pojo);	}	public void deleteDB(Account pojo) throws Exception {		logger.debug("AccountDAO deleteAccount");		delete(pojo);	}	public void upsertDB(Account pojo) throws Exception {		logger.debug("AccountDAO upsertAccount");		upsert(pojo);	}}