package com.sing.ren.dao.table;import java.util.List;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.Achievement;@Componentpublic class AchievementDAO extends BaseDao {	@Override	public String statementIdForInsert() throws Exception {		return nsForTableStatements("achievement_insert");	}	@Override	public String statementIdForQuery() throws Exception {		return nsForTableStatements("achievement_query");	}	@Override	public String statementIdForUpdate() throws Exception {		return nsForTableStatements("achievement_update");	}	@Override	public String statementIdForUpsert() throws Exception {		return nsForTableStatements("achievement_upsert");	}	@Override	public String statementIdForDelete() throws Exception {		return nsForTableStatements("achievement_delete");	}	public void insertDB(Achievement pojo) throws Exception {		logger.debug("AchievementDAO insertAchievement");		insert(pojo);	}	@SuppressWarnings("unchecked")	public List<Achievement> queryDB(Achievement pojo) throws Exception {		logger.debug("AchievementDAO queryAchievement");		return (List<Achievement>) query(pojo);	}	public void updateDB(Achievement pojo) throws Exception{		logger.debug("AchievementDAO updateAchievement");		upsert(pojo);	}	public void deleteDB(Achievement pojo) throws Exception {		logger.debug("AchievementDAO deleteAchievement");		delete(pojo);	}	public void upsertDB(Achievement pojo) throws Exception {		logger.debug("AchievementDAO upsertAchievement");		upsert(pojo);	}}