package com.sing.ren.dao.table;import java.util.ArrayList;import java.util.List;import java.util.Map;import org.apache.commons.collections.MapUtils;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.Achievement;@Componentpublic class AchievementDAO extends BaseDao {	@SuppressWarnings("deprecation")	public void insertDB(Achievement dto) throws Exception {		logger.debug("AchievementDAO insertAchievement");		getSqlMapClientTemplate().insert( nsForTableStatements("achievement_insert"), dto);	}	public List<Achievement> getAll(Map<String, Object> map) throws Exception {		logger.debug("AchievementDAO getAll");		List<Map<String, Object>> resultMapList = query(map, nsForTableStatements("achievement_query"));		List<Achievement> result = new ArrayList<Achievement>();		for (Map<String, Object> resultMap : resultMapList){			result.add( mapToObject(resultMap) );		}		return result;	}	public void deleteDB(Map<String, Object> map) throws Exception {		logger.debug("AchievementDAO deleteAchievement");		delete(map, nsForTableStatements("achievement_delete"));	}	public Achievement mapToObject(Map<String, Object> map) throws Exception {		Achievement obj = new Achievement();		obj.setAchievementId( MapUtils.getInteger(map, "achievement_id") );		obj.setStudentId( MapUtils.getString(map, "student_id", "") );		obj.setGrade( MapUtils.getString(map, "grade", "") );		obj.setScore( MapUtils.getString(map, "score", "") );		obj.setDate( MapUtils.getString(map, "date", "") );		obj.setUpdater( MapUtils.getString(map, "updater", "") );		obj.setUpdateTime( MapUtils.getString(map, "update_time", "") ); 	return obj;	}}