package com.sing.ren.dao.table;import java.util.List;import java.util.Map;import org.apache.commons.collections.MapUtils;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.Achievement;@Componentpublic class AchievementDAO extends BaseDao {	@SuppressWarnings("deprecation")	public List<Achievement> query(Achievement dto) throws Exception {		logger.debug("AchievementDAO queryAchievement");		return getSqlMapClientTemplate().queryForList(nsForTableStatements("achievement_query"), dto);	}	@SuppressWarnings("deprecation")	public void insert(Achievement dto) throws Exception {		logger.debug("AchievementDAO insertAchievement");		getSqlMapClientTemplate().insert( nsForTableStatements("achievement_insert"), dto);	}	@SuppressWarnings("deprecation")	public void update(Achievement dto) throws Exception {		logger.debug("AchievementDAO updateAchievement");		getSqlMapClientTemplate().queryForList(nsForTableStatements("achievement_update"), dto);	}	@SuppressWarnings("deprecation")	public void delete(Achievement dto) throws Exception {		logger.debug("AchievementDAO AchievementDetail");		getSqlMapClientTemplate().queryForList(nsForTableStatements("achievement_delete"), dto);	}	public Achievement mapToObject(Map<String, Object> map) throws Exception {		Achievement obj = new Achievement();		obj.setAchievementId( MapUtils.getInteger(map, "achievement_id") );		obj.setStudentId( MapUtils.getString(map, "student_id", "") );		obj.setGrade( MapUtils.getString(map, "grade", "") );		obj.setScore( MapUtils.getString(map, "score", "") );		obj.setDate( MapUtils.getString(map, "date", "") );		obj.setUpdater( MapUtils.getString(map, "updater", "") );		obj.setUpdateTime( MapUtils.getString(map, "update_time", "") );	return obj;	}}