package com.sing.ren.dao.table;import java.util.List;import java.util.Map;import org.apache.commons.collections.MapUtils;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.AchievementLog;@Componentpublic class AchievementLogDAO extends BaseDao {	@SuppressWarnings("deprecation")	public List<AchievementLog> query(AchievementLog dto) throws Exception {		logger.debug("AchievementLogDAO queryAchievementLog");		return getSqlMapClientTemplate().queryForList(nsForTableStatements("achievement_log_query"), dto);	}	@SuppressWarnings("deprecation")	public void insert(AchievementLog dto) throws Exception {		logger.debug("AchievementLogDAO insertAchievementLog");		getSqlMapClientTemplate().insert( nsForTableStatements("achievement_log_insert"), dto);	}	@SuppressWarnings("deprecation")	public void update(AchievementLog dto) throws Exception {		logger.debug("AchievementLogDAO updateAchievementLog");		getSqlMapClientTemplate().queryForList(nsForTableStatements("achievement_log_update"), dto);	}	@SuppressWarnings("deprecation")	public void delete(AchievementLog dto) throws Exception {		logger.debug("AchievementLogDAO AchievementLogDetail");		getSqlMapClientTemplate().queryForList(nsForTableStatements("achievement_log_delete"), dto);	}	public AchievementLog mapToObject(Map<String, Object> map) throws Exception {		AchievementLog obj = new AchievementLog();		obj.setAchievementLogId( MapUtils.getInteger(map, "achievement_log_id") );		obj.setAchievementId( MapUtils.getInteger(map, "achievement_id") );		obj.setStudentId( MapUtils.getString(map, "student_id", "") );		obj.setGrade( MapUtils.getString(map, "grade", "") );		obj.setScore( MapUtils.getString(map, "score", "") );		obj.setDate( MapUtils.getString(map, "date", "") );		obj.setUpdater( MapUtils.getString(map, "updater", "") );		obj.setUpdateTime( MapUtils.getString(map, "update_time", "") );	return obj;	}}