package com.sing.ren.dao.table;import java.util.ArrayList;import java.util.List;import java.util.Map;import org.apache.commons.collections.MapUtils;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.AchievementLog;@Componentpublic class AchievementLogDAO extends BaseDao {	@SuppressWarnings("deprecation")	public void insertDB(AchievementLog dto) throws Exception {		logger.debug("AchievementLogDAO insertAchievementLog");		getSqlMapClientTemplate().insert( nsForTableStatements("achievement_log_insert"), dto);	}	public List<AchievementLog> getAll(Map<String, Object> map) throws Exception {		logger.debug("AchievementLogDAO getAll");		List<Map<String, Object>> resultMapList = query(map, nsForTableStatements("achievement_log_query"));		List<AchievementLog> result = new ArrayList<AchievementLog>();		for (Map<String, Object> resultMap : resultMapList){			result.add( mapToObject(resultMap) );		}		return result;	}	public void deleteDB(Map<String, Object> map) throws Exception {		logger.debug("AchievementLogDAO deleteAchievementLog");		delete(map, nsForTableStatements("achievement_log_delete"));	}	public AchievementLog mapToObject(Map<String, Object> map) throws Exception {		AchievementLog obj = new AchievementLog();		obj.setAchievementLogId( MapUtils.getInteger(map, "achievement_log_id") );		obj.setAchievementId( MapUtils.getInteger(map, "achievement_id") );		obj.setStudentId( MapUtils.getString(map, "student_id", "") );		obj.setGrade( MapUtils.getString(map, "grade", "") );		obj.setScore( MapUtils.getString(map, "score", "") );		obj.setDate( MapUtils.getString(map, "date", "") );		obj.setUpdater( MapUtils.getString(map, "updater", "") );		obj.setUpdateTime( MapUtils.getString(map, "update_time", "") ); 	return obj;	}}