package com.sing.ren.dao.table;import java.util.List;import java.util.Map;import org.apache.commons.collections.MapUtils;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.Bill;@Componentpublic class BillDAO extends BaseDao {	@SuppressWarnings("deprecation")	public List<Bill> query(Bill dto) throws Exception {		logger.debug("BillDAO queryBill");		return getSqlMapClientTemplate().queryForList(nsForTableStatements("bill_query"), dto);	}	@SuppressWarnings("deprecation")	public void insert(Bill dto) throws Exception {		logger.debug("BillDAO insertBill");		getSqlMapClientTemplate().insert( nsForTableStatements("bill_insert"), dto);	}	@SuppressWarnings("deprecation")	public void update(Bill dto) throws Exception {		logger.debug("BillDAO updateBill");		getSqlMapClientTemplate().queryForList(nsForTableStatements("bill_update"), dto);	}	@SuppressWarnings("deprecation")	public void delete(Bill dto) throws Exception {		logger.debug("BillDAO BillDetail");		getSqlMapClientTemplate().queryForList(nsForTableStatements("bill_delete"), dto);	}	public Bill mapToObject(Map<String, Object> map) throws Exception {		Bill obj = new Bill();		obj.setBillId( MapUtils.getInteger(map, "bill_id") );		obj.setClassMasterId( MapUtils.getInteger(map, "class_master_id") );		obj.setStudentId( MapUtils.getString(map, "student_id", "") );		obj.setDiscount( MapUtils.getString(map, "discount", "") );		obj.setPayment( MapUtils.getString(map, "payment", "") );		obj.setPayMethod( MapUtils.getString(map, "pay_method", "") );		obj.setPayDate( MapUtils.getString(map, "pay_date", "") );		obj.setEntryDate( MapUtils.getString(map, "entry_date", "") );		obj.setUpdater( MapUtils.getString(map, "updater", "") );		obj.setUpdateTime( MapUtils.getString(map, "update_time", "") );	return obj;	}}