package com.sing.ren.dao.table;import java.util.List;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.Bill;@Componentpublic class BillDAO extends BaseDao {	@Override	public String statementIdForInsert() throws Exception {		return nsForTableStatements("bill_insert");	}	@Override	public String statementIdForQuery() throws Exception {		return nsForTableStatements("bill_query");	}	@Override	public String statementIdForUpdate() throws Exception {		return nsForTableStatements("bill_update");	}	@Override	public String statementIdForUpsert() throws Exception {		return nsForTableStatements("bill_upsert");	}	@Override	public String statementIdForDelete() throws Exception {		return nsForTableStatements("bill_delete");	}	public void insertDB(Bill pojo) throws Exception {		logger.debug("BillDAO insertBill");		insert(pojo);	}	@SuppressWarnings("unchecked")	public List<Bill> queryDB(Bill pojo) throws Exception {		logger.debug("BillDAO queryBill");		return (List<Bill>) query(pojo);	}	public void updateDB(Bill pojo) throws Exception{		logger.debug("BillDAO updateBill");		upsert(pojo);	}	public void deleteDB(Bill pojo) throws Exception {		logger.debug("BillDAO deleteBill");		delete(pojo);	}	public void upsertDB(Bill pojo) throws Exception {		logger.debug("BillDAO upsertBill");		upsert(pojo);	}}