package com.sing.ren.dao.table;import java.util.ArrayList;import java.util.List;import java.util.Map;import org.apache.commons.collections.MapUtils;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.Bill;@Componentpublic class BillDAO extends BaseDao {	@SuppressWarnings("deprecation")	public void insertDB(Bill dto) throws Exception {		logger.debug("BillDAO insertBill");		getSqlMapClientTemplate().insert( nsForTableStatements("bill_insert"), dto);	}	public List<Bill> getAll(Map<String, Object> map) throws Exception {		logger.debug("BillDAO getAll");		List<Map<String, Object>> resultMapList = query(map, nsForTableStatements("bill_query"));		List<Bill> result = new ArrayList<Bill>();		for (Map<String, Object> resultMap : resultMapList){			result.add( mapToObject(resultMap) );		}		return result;	}	public void deleteDB(Map<String, Object> map) throws Exception {		logger.debug("BillDAO deleteBill");		delete(map, nsForTableStatements("bill_delete"));	}	public Bill mapToObject(Map<String, Object> map) throws Exception {		Bill obj = new Bill();		obj.setBillId( MapUtils.getInteger(map, "bill_id") );		obj.setClassMasterId( MapUtils.getInteger(map, "class_master_id") );		obj.setStudentId( MapUtils.getString(map, "student_id", "") );		obj.setDiscount( MapUtils.getString(map, "discount", "") );		obj.setPayment( MapUtils.getString(map, "payment", "") );		obj.setPayMethod( MapUtils.getString(map, "pay_method", "") );		obj.setPayDate( MapUtils.getString(map, "pay_date", "") );		obj.setEntryDate( MapUtils.getString(map, "entry_date", "") );		obj.setUpdater( MapUtils.getString(map, "updater", "") );		obj.setUpdateTime( MapUtils.getString(map, "update_time", "") ); 	return obj;	}}