package com.sing.ren.dao.table;import java.util.List;import java.util.Map;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.Bill;@Componentpublic class BillDAO extends BaseDao {	@Override	public String statementIdForInsert() throws Exception {		return nsForTableStatements("bill_insert");	}	@Override	public String statementIdForQuery() throws Exception {		return nsForTableStatements("bill_query");	}	@Override	public String statementIdForUpdate() throws Exception {		return nsForTableStatements("bill_update");	}	@Override	public String statementIdForUpsert() throws Exception {		return nsForTableStatements("bill_upsert");	}	@Override	public String statementIdForDelete() throws Exception {		return nsForTableStatements("bill_delete");	}	public void insertDB(Map<String,Object> map) throws Exception {		logger.debug("BillDAO insertBill");		insert(map);	}	@SuppressWarnings("unchecked")	public List<Bill> queryDB(Map<String,Object> map) throws Exception {		logger.debug("BillDAO queryBill");		return (List<Bill>) query(map);	}	public void updateDB(Map<String,Object> map) throws Exception{		logger.debug("BillDAO updateBill");		upsert(map);	}	public void deleteDB(Map<String,Object> map) throws Exception {		logger.debug("BillDAO deleteBill");		delete(map);	}	public void upsertDB(Map<String,Object> map) throws Exception {		logger.debug("BillDAO upsertBill");		upsert(map);	}}