package com.sing.ren.dao.table;import java.util.ArrayList;import java.util.List;import java.util.Map;import org.apache.commons.collections.MapUtils;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.BillLog;@Componentpublic class BillLogDAO extends BaseDao {	@SuppressWarnings("deprecation")	public void insertDB(BillLog dto) throws Exception {		logger.debug("BillLogDAO insertBillLog");		getSqlMapClientTemplate().insert( nsForTableStatements("bill_log_insert"), dto);	}	public List<BillLog> getAll(Map<String, Object> map) throws Exception {		logger.debug("BillLogDAO getAll");		List<Map<String, Object>> resultMapList = query(map, nsForTableStatements("bill_log_query"));		List<BillLog> result = new ArrayList<BillLog>();		for (Map<String, Object> resultMap : resultMapList){			result.add( mapToObject(resultMap) );		}		return result;	}	public void deleteDB(Map<String, Object> map) throws Exception {		logger.debug("BillLogDAO deleteBillLog");		delete(map, nsForTableStatements("bill_log_delete"));	}	public BillLog mapToObject(Map<String, Object> map) throws Exception {		BillLog obj = new BillLog();		obj.setBillLogId( MapUtils.getInteger(map, "bill_log_id") );		obj.setBillId( MapUtils.getInteger(map, "bill_id") );		obj.setClassMasterId( MapUtils.getString(map, "class_master_id", "") );		obj.setStudentId( MapUtils.getString(map, "student_id", "") );		obj.setDiscount( MapUtils.getString(map, "discount", "") );		obj.setPayment( MapUtils.getString(map, "payment", "") );		obj.setPayMethod( MapUtils.getString(map, "pay_method", "") );		obj.setPayDate( MapUtils.getString(map, "pay_date", "") );		obj.setEntryDate( MapUtils.getString(map, "entry_date", "") );		obj.setUpdater( MapUtils.getString(map, "updater", "") );		obj.setUpdateTime( MapUtils.getString(map, "update_time", "") ); 	return obj;	}}