package com.sing.ren.dao.table;import java.util.List;import java.util.Map;import org.apache.commons.collections.MapUtils;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.BillLog;@Componentpublic class BillLogDAO extends BaseDao {	@SuppressWarnings("deprecation")	public List<BillLog> query(BillLog dto) throws Exception {		logger.debug("BillLogDAO queryBillLog");		return getSqlMapClientTemplate().queryForList(nsForTableStatements("bill_log_query"), dto);	}	@SuppressWarnings("deprecation")	public void insert(BillLog dto) throws Exception {		logger.debug("BillLogDAO insertBillLog");		getSqlMapClientTemplate().insert( nsForTableStatements("bill_log_insert"), dto);	}	@SuppressWarnings("deprecation")	public void update(BillLog dto) throws Exception {		logger.debug("BillLogDAO updateBillLog");		getSqlMapClientTemplate().queryForList(nsForTableStatements("bill_log_update"), dto);	}	@SuppressWarnings("deprecation")	public void delete(BillLog dto) throws Exception {		logger.debug("BillLogDAO BillLogDetail");		getSqlMapClientTemplate().queryForList(nsForTableStatements("bill_log_delete"), dto);	}	public BillLog mapToObject(Map<String, Object> map) throws Exception {		BillLog obj = new BillLog();		obj.setBillLogId( MapUtils.getInteger(map, "bill_log_id") );		obj.setBillId( MapUtils.getInteger(map, "bill_id") );		obj.setClassMasterId( MapUtils.getString(map, "class_master_id", "") );		obj.setStudentId( MapUtils.getString(map, "student_id", "") );		obj.setDiscount( MapUtils.getString(map, "discount", "") );		obj.setPayment( MapUtils.getString(map, "payment", "") );		obj.setPayMethod( MapUtils.getString(map, "pay_method", "") );		obj.setPayDate( MapUtils.getString(map, "pay_date", "") );		obj.setEntryDate( MapUtils.getString(map, "entry_date", "") );		obj.setUpdater( MapUtils.getString(map, "updater", "") );		obj.setUpdateTime( MapUtils.getString(map, "update_time", "") );	return obj;	}}