package com.sing.ren.dao.table;import java.util.List;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.ClassDetail;@Componentpublic class ClassDetailDAO extends BaseDao {	@Override	public String statementIdForInsert() throws Exception {		return nsForTableStatements("class_detail_insert");	}	@Override	public String statementIdForQuery() throws Exception {		return nsForTableStatements("class_detail_query");	}	@Override	public String statementIdForUpdate() throws Exception {		return nsForTableStatements("class_detail_update");	}	@Override	public String statementIdForUpsert() throws Exception {		return nsForTableStatements("class_detail_upsert");	}	@Override	public String statementIdForDelete() throws Exception {		return nsForTableStatements("class_detail_delete");	}	public void insertDB(ClassDetail pojo) throws Exception {		logger.debug("ClassDetailDAO insertClassDetail");		insert(pojo);	}	@SuppressWarnings("unchecked")	public List<ClassDetail> queryDB(ClassDetail pojo) throws Exception {		logger.debug("ClassDetailDAO queryClassDetail");		return (List<ClassDetail>) query(pojo);	}	public void updateDB(ClassDetail pojo) throws Exception{		logger.debug("ClassDetailDAO updateClassDetail");		upsert(pojo);	}	public void deleteDB(ClassDetail pojo) throws Exception {		logger.debug("ClassDetailDAO deleteClassDetail");		delete(pojo);	}		public void upsertDB(ClassDetail pojo) throws Exception {		logger.debug("ClassDetailDAO upsertClassDetail");		upsert(pojo);	}}