package com.sing.ren.dao.table;import java.text.SimpleDateFormat;import java.util.Calendar;import java.util.List;import java.util.Map;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.ClassDetail;@Componentpublic class ClassDetailDAO extends BaseDao {	@Override	public String statementIdForInsert() throws Exception {		return nsForTableStatements("class_detail_insert");	}	@Override	public String statementIdForQuery() throws Exception {		return nsForTableStatements("class_detail_query");	}	@Override	public String statementIdForUpdate() throws Exception {		return nsForTableStatements("class_detail_update");	}	@Override	public String statementIdForUpsert() throws Exception {		return nsForTableStatements("class_detail_upsert");	}	@Override	public String statementIdForDelete() throws Exception {		return nsForTableStatements("class_detail_delete");	}	public void insertDB(Map<String,Object> map) throws Exception {		logger.debug("ClassDetailDAO insertClassDetail");		SimpleDateFormat sdf = new SimpleDateFormat("yyyy/MM/dd HH:mm:ss");		map.put("update_time", sdf.format(Calendar.getInstance().getTime()));		insert(map);	}	@SuppressWarnings("unchecked")	public List<Map<String,Object>> queryDB(Map<String,Object> map) throws Exception {		logger.debug("ClassDetailDAO queryClassDetail");		return (List<Map<String,Object>>) query(map);	}	public void updateDB(Map<String,Object> map) throws Exception{		logger.debug("ClassDetailDAO updateClassDetail");		update(map);	}	public void deleteDB(Map<String,Object> map) throws Exception {		logger.debug("ClassDetailDAO deleteClassDetail");		delete(map);	}	public void upsertDB(Map<String,Object> map) throws Exception {		logger.debug("ClassDetailDAO upsertClassDetail");		upsert(map);	}}