package com.sing.ren.dao.table;import java.util.ArrayList;import java.util.List;import java.util.Map;import org.apache.commons.collections.MapUtils;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.ClassMaster;@Componentpublic class ClassMasterDAO extends BaseDao {	@SuppressWarnings("deprecation")	public void insertDB(ClassMaster dto) throws Exception {		logger.debug("ClassMasterDAO insertClassMaster");		getSqlMapClientTemplate().insert( nsForTableStatements("class_master_insert"), dto);	}	public List<ClassMaster> getAll(Map<String, Object> map) throws Exception {		logger.debug("ClassMasterDAO getAll");		List<Map<String, Object>> resultMapList = query(map, nsForTableStatements("class_master_query"));		List<ClassMaster> result = new ArrayList<ClassMaster>();		for (Map<String, Object> resultMap : resultMapList){			result.add( mapToObject(resultMap) );		}		return result;	}	public void deleteDB(Map<String, Object> map) throws Exception {		logger.debug("ClassMasterDAO deleteClassMaster");		delete(map, nsForTableStatements("class_master_delete"));	}	public ClassMaster mapToObject(Map<String, Object> map) throws Exception {		ClassMaster obj = new ClassMaster();		obj.setClassMasterId( MapUtils.getInteger(map, "class_master_id") );		obj.setName( MapUtils.getString(map, "name", "") );		obj.setType( MapUtils.getString(map, "type", "") );		obj.setLevel( MapUtils.getString(map, "level", "") );		obj.setStatus( MapUtils.getString(map, "status", "") );		obj.setPrice( MapUtils.getString(map, "price", "") );		obj.setSummary( MapUtils.getString(map, "summary", "") );		obj.setCount( MapUtils.getString(map, "count", "") );		obj.setRest( MapUtils.getString(map, "rest", "") );		obj.setSDate( MapUtils.getString(map, "s_date", "") );		obj.setEDate( MapUtils.getString(map, "e_date", "") );		obj.setPlace( MapUtils.getString(map, "place", "") );		obj.setTeacherId( MapUtils.getString(map, "teacher_id", "") );		obj.setUpdater( MapUtils.getString(map, "updater", "") );		obj.setUpdateTime( MapUtils.getString(map, "update_time", "") ); 	return obj;	}}