package com.sing.ren.dao.table;import java.util.List;import org.springframework.stereotype.Component;import com.sing.ren.dao.BaseDao;import com.sing.ren.pojo.Person;@Componentpublic class PersonDAO extends BaseDao {	@Override	public String statementIdForInsert() throws Exception {		return nsForTableStatements("person_insert");	}	@Override	public String statementIdForQuery() throws Exception {		return nsForTableStatements("person_query");	}	@Override	public String statementIdForUpdate() throws Exception {		return nsForTableStatements("person_update");	}	@Override	public String statementIdForUpsert() throws Exception {		return nsForTableStatements("person_upsert");	}	@Override	public String statementIdForDelete() throws Exception {		return nsForTableStatements("person_delete");	}	public void insertDB(Person pojo) throws Exception {		logger.debug("PersonDAO insertPerson");		insert(pojo);	}	@SuppressWarnings("unchecked")	public List<Person> queryDB(Person pojo) throws Exception {		logger.debug("PersonDAO queryPerson");		return (List<Person>) query(pojo);	}	public void updateDB(Person pojo) throws Exception{		logger.debug("PersonDAO updatePerson");		upsert(pojo);	}	public void deleteDB(Person pojo) throws Exception {		logger.debug("PersonDAO deletePerson");		delete(pojo);	}	public void upsertDB(Person pojo) throws Exception {		logger.debug("PersonDAO upsertPerson");		upsert(pojo);	}}