package com.sing.ren.pojo;public class Bill {	private Integer billId;	private Integer classMasterId;	private String studentId;	private String discount;	private String payment;	private String payMethod;	private String payDate;	private String entryDate;	private String updater;	private String updateTime;	public Integer getBillId() {		return billId;	}	public void setBillId(Integer billId) {		this.billId = billId;	}	public Integer getClassMasterId() {		return classMasterId;	}	public void setClassMasterId(Integer classMasterId) {		this.classMasterId = classMasterId;	}	public String getStudentId() {		return studentId;	}	public void setStudentId(String studentId) {		this.studentId = studentId;	}	public String getDiscount() {		return discount;	}	public void setDiscount(String discount) {		this.discount = discount;	}	public String getPayment() {		return payment;	}	public void setPayment(String payment) {		this.payment = payment;	}	public String getPayMethod() {		return payMethod;	}	public void setPayMethod(String payMethod) {		this.payMethod = payMethod;	}	public String getPayDate() {		return payDate;	}	public void setPayDate(String payDate) {		this.payDate = payDate;	}	public String getEntryDate() {		return entryDate;	}	public void setEntryDate(String entryDate) {		this.entryDate = entryDate;	}	public String getUpdater() {		return updater;	}	public void setUpdater(String updater) {		this.updater = updater;	}	public String getUpdateTime() {		return updateTime;	}	public void setUpdateTime(String updateTime) {		this.updateTime = updateTime;	}}